export const initialState = {
    username: '',
    password: '',
    email: ''
}

export const reducer = (state, action) => {
    switch (action.type) {
        case 'username':
            return { ...state, username: action.value }
        case 'password':
            return { ...state, password: action.value }
        case 'email':
            return { ...state, email: action.value }
        default:
            return state
    }
}
