export const initialState = {
    username: '',
    password: '',
    email: ''
}

const CONSTANT = {
    USERNAME: 'username',
    PASSWORD: 'password',
    EMAIL: 'email'
}
export const reducer = (state, action) => {
    switch (action.type) {
        case CONSTANT.USERNAME:
            return { ...state, username: action.value }
        case CONSTANT.PASSWORD:
            return { ...state, password: state.password }
        case CONSTANT.EMAIL:
            return { ...state, email: state.email }
        default:
            return state
    }
}
